const payload = JSON.parse(msg.payload)
const op = payload.op
node.log(op)
if (op === "service_response") {
    // get joystick token
    const joystick = payload.values.joystick_token

    flow.set("joystick_token", joystick)

    msg.payload = "got joystick token :" + joystick
} else if (op === "publish") {
    const topic = payload.topic
    if (topic === "/diagnostics_agg") {
        // get hardware health
        let cellValues = payload.msg.status.filter((obj) => obj.name === "/Power System/Battery Management System")[0].values
        cellValues = cellValues.filter((val) => val.key.includes("Cell voltage"))
        cellValues = cellValues.map((val) => ({
            value: val.value,
            cell: JSON.parse(val.key).args.cell
        }))
        msg.payload = cellValues
    }
}


return msg;

